import React, { useState } from 'react';


const Like = ({ artwork, likedItems }) => {
  const [currentItem, setCurrentItem] = useState(0);

  const filteredItems = likedItems ? likedItems.filter((item) => item.liked) : [];

  const handlePrevClick = () => {
    setCurrentItem((prevItem) => (prevItem === 0 ? filteredItems.length - 1 : prevItem - 1));
  };

  const handleNextClick = () => {
    setCurrentItem((prevItem) => (prevItem === filteredItems.length - 1 ? 0 : prevItem + 1));
  };

  return (
    <div className="like-container" aria-label="Liked artwork">
      {filteredItems.length > 0 ? (
        <>
          <i className="fas fa-heart" style={{ color: 'pink' }} aria-label="Liked icon"></i>
          <p className="like-text" aria-label="Liked text">You liked these artworks:</p>
          <div className="carousel">
            <button className="prev-btn" onClick={handlePrevClick} aria-label="Previous button">
              &#10094;
            </button>
            <div className="carousel-items">
              {filteredItems.map((item, index) => (
                <div key={item.artwork} className={`carousel-item ${index === currentItem ? 'active' : ''}`}>
                  <img src={item.artwork} alt={item.title} className="carousel-img" />
                  <p className="carousel-item-title" aria-label="Liked artwork title">
                    {item.title}
                  </p>
                </div>
              ))}
            </div>
            <button className="next-btn" onClick={handleNextClick} aria-label="Next button">
              &#10095;
            </button>
          </div>
        </>
      ) : (
        <p className="like-text" aria-label="Liked text">You haven't liked any artworks yet!</p>
      )}
    </div>
  );

      }
export default Like;
