import React, { useState } from "react";
import Gallery from "./galleryArt.json";

const Liked = ({ likedItems, handleLike }) => {
  return (
    <div className="liked-items-container">
      <h2 className="filter-title liked-items-title">Liked Items</h2>
      <div className="liked-items-carousel">
        {likedItems.map((item) => (
          <div className="liked-item" key={item.artwork}>
            <img
              src={item.artwork}
              alt={item.title}
              className="img-fluid"
              aria-label="Image of liked artwork"
            />
            <p
              className="liked-item-title"
              aria-label="Title of liked art piece"
            >
              {item.title}
            </p>
            <button
              className="remove-like"
              aria-label="Remove like button to remove saved art"
              onClick={() => handleLike(item.title, item.artwork)}
            >
              <i className="fa fa-times"></i>
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const SearchFilter = () => {
  const [selected, listed] = useState(Gallery);
  const [likedItems, setLikedItems] = useState([]);

  const filterMedium = (category) => {
    const update = Gallery.filter((button) => {
      return button.medium === category;
    });
    listed(update);
  };

  const handleLike = (title, artwork) => {
    const newItem = { title: title, artwork: artwork };
    const index = likedItems.findIndex((item) => item.artwork === artwork);
    if (index > -1) {
      // item already in liked items, unheart it
      const updatedItems = [...likedItems];
      updatedItems.splice(index, 1);
      setLikedItems(updatedItems);
    } else {
      // item not in liked items, heart it
      setLikedItems([...likedItems, newItem]);
    }
  };

  return (
    <main className="main-filter">
      <h1 className="filter-title heading color-change">Gallery</h1>
      <h2 className="filter-title art">
        Search <span className="line-art">art </span>by{" "}
        <span className="line-art">medium</span>.
      </h2>
      <section id="medium-container" role="menu">
        <button
          id="Paint"
          className="btn filter-btn"
          onClick={() => filterMedium("Paint")}
        >
          PAINT
        </button>
        <button
          id="Mixed Media"
          className="btn filter-btn"
          onClick={() => filterMedium("Mixed Media")}
        >
          MIXED MEDIA
        </button>
        <button
          id="Chromolithography"
          className="btn filter-btn"
          onClick={() => filterMedium("Chromolithography")}
        >
          CHROMOLITHOGRAPHY
        </button>
        <button
          id="Sculpture"
          className="btn filter-btn"
          onClick={() => filterMedium("Sculpture")}
        >
          SCULPTURE
        </button>
        <button
          id="Acrylics"
          className="btn filter-btn"
          onClick={() => filterMedium("Print")}
        >
          PRINT
        </button>
        <button
          id="Water Color"
          className="btn filter-btn"
          onClick={() => filterMedium("Water Color")}
        >
          WATER COLOR
        </button>
        <button
          id="ShowAll"
          className="btn
          filter-btn"
          onClick={() => listed(Gallery)}
          >
          SHOW ALL
          </button>
          </section>
          <Liked likedItems={likedItems} handleLike={handleLike} />
          <div className="gallery-container">
          {selected.map((item) => (
          <div className="gallery-item" key={item.artwork}>
          <img
                     src={item.artwork}
                     alt={item.title}
                     className="img-fluid"
                     aria-label="Image of art piece"
                   />
          <p className="gallery-item-title" aria-label="Title of art piece">
          {item.title}
          </p>
          <button
          className="like"
          aria-label="Heart button to save art"
          onClick={() => handleLike(item.title, item.artwork)}
          >
          <i className={likedItems.find((el) => el.artwork === item.artwork) ? "fa fa-heart" : "far fa-heart"}></i>
          </button>
          </div>
          ))}
          </div>
          </main>
          );
          };

          export default SearchFilter;
